const useDashboardData = () => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const socket = io('/dashboard');

    socket.on('analytics-update', (newData) => {
      setData(newData);
      setLoading(false);
    });

    return () => socket.disconnect();
  }, []);

  return { data, loading };
};

// Drag and drop task management
const TaskBoard = () => {
  const [tasks, setTasks] = useState([]);

  const onDragEnd = (result) => {
    if (!result.destination) return;

    const items = Array.from(tasks);
    const [reorderedItem] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reorderedItem);

    setTasks(items);
    updateTaskOrder(items);
  };

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      {/* Task columns */}
    </DragDropContext>
  );
};

// Weather data fetching hook
const useWeatherData = (location) => {
  const [weather, setWeather] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchWeather = async () => {
      try {
        setLoading(true);
        const response = await weatherAPI.getForecast(location);
        setWeather(response.data);
        setError(null);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    if (location) fetchWeather();
  }, [location]);

  return { weather, loading, error };
};

// Social media post scheduler
const usePostScheduler = () => {
  const [scheduledPosts, setScheduledPosts] = useState([]);

  const schedulePost = async (postData, scheduledTime) => {
    try {
      const response = await api.post('/schedule', {
        ...postData,
        scheduledTime,
        platforms: postData.selectedPlatforms
      });

      setScheduledPosts(prev => [...prev, response.data]);
      return response.data;
    } catch (error) {
      throw new Error('Failed to schedule post');
    }
  };

  return { scheduledPosts, schedulePost };
};

// Smooth scroll animation hook
const useScrollAnimation = () => {
  const [scrollY, setScrollY] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      setScrollY(window.scrollY);
    };

    window.addEventListener('scroll', handleScroll, { passive: true });
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return scrollY;
};

// Recipe search with ingredients
const searchRecipesByIngredients = async (ingredients) => {
  try {
    const response = await recipeAPI.findByIngredients({
      ingredients: ingredients.join(','),
      number: 12,
      ranking: 1
    });

    return response.data.map(recipe => ({
      id: recipe.id,
      title: recipe.title,
      image: recipe.image,
      usedIngredients: recipe.usedIngredients,
      missedIngredients: recipe.missedIngredients
    }));
  } catch (error) {
    throw new Error('Failed to fetch recipes');
  }
};

