// Optimistic update for story verification
const { mutate: verifyStory } = useMutation({
  mutationFn: (storyId: string) => api.verifyStory(storyId),
  onMutate: async (storyId) => {
    await queryClient.cancelQueries(['stories']);

    const previous = queryClient.getQueryData(['stories']);
    queryClient.setQueryData(['stories'], (old: Story[]) =>
      old.map(story =>
        story.id === storyId
          ? { ...story, verified: true }
          : story
      )
    );

    return { previous };
  },
  onError: (err, variables, context) => {
    queryClient.setQueryData(['stories'], context.previous);
  },
});

// Reusable Button component with TypeScript
interface ButtonProps extends React.ButtonHTMLAttributes<HTMLButtonElement> {
  variant?: 'primary' | 'secondary' | 'outline' | 'ghost';
  size?: 'sm' | 'md' | 'lg';
  isLoading?: boolean;
  leftIcon?: React.ReactNode;
  rightIcon?: React.ReactNode;
}

export const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ 
    variant = 'primary', 
    size = 'md', 
    isLoading, 
    leftIcon, 
    rightIcon,
    children,
    className,
    disabled,
    ...props 
  }, ref) => {
    return (
      <button
        ref={ref}
        disabled={disabled || isLoading}
        className={cn(
          buttonVariants[variant],
          buttonSizes[size],
          isLoading && 'opacity-70 cursor-not-allowed',
          className
        )}
        {...props}
      >
        {isLoading ? (
          <Spinner size={size} />
        ) : (
          <>
            {leftIcon && <span className="mr-2">{leftIcon}</span>}
            {children}
            {rightIcon && <span className="ml-2">{rightIcon}</span>}
          </>
        )}
      </button>
    );
  }
);

